import { useState } from 'react'

const Button = ({ handleClick, text }) => (
  <button onClick={handleClick}>
    {text}
  </button>
)

const Statistics = (props) => {
  console.log(ele.good)
  return(
  <div>
    <h2>statistics</h2>
    <p>
      good {props.ele.good}<br />
      neutral {props.ele.neutral}<br />
      {/*bad {props.bad}<br />
      all {props.all} <br />
      avergae {(props.good * 1 - props.bad * 1) / props.all} <br />
  positive {(props.good / props.all) * 100}%*/}
    </p>
  </div>)
}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const [all, setAll] = useState(0)
  const ele = {good, neutral, bad, all}

  const handleGood = () => {
    const updatedGood = good + 1
    setGood(updatedGood)
    setAll(updatedGood + bad + neutral)
  }

  const handleBad = () => {
    const updatedBad = bad + 1
    setBad(updatedBad)
    setAll(good + updatedBad + neutral)
  }

  const handleNeutral = () => {
    const updatedNeutral = neutral + 1
    setNeutral(updatedNeutral)
    setAll(good + bad + updatedNeutral)
  }

  return (
    <div>
      <h2>give feedback</h2>
      <Button handleClick={handleGood} text='good' />
      <Button handleClick={handleNeutral} text='neutral' />
      <Button handleClick={handleBad} text='bad' />
      <Statistics good = {good} bad = {bad} neutral = {neutral} all = {all}/>
    </div>
  )
}

export default App
